package JavaBasics;

public class Datatypes {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		//Data type
		// integer
		int i = 10;
		i = 20;
		System.out.println(i);
		int g = 33;
		
		
		//boolean
		boolean a = true;
		boolean b = false;
		
		//character 
		char c = 's';
		char d = 'A';
		
		//String
		
		String e = "Anjali";
		String f = "I like IT";
		System.out.println(e);
		System.out.println(i+g);
	}

}
