package JavaBasics;

public class IfElseConcept {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int a = 100;
		int b = 300;
		
		if(a>b) {
			System.out.println(a);
		}
		else {
			System.out.println(b);
	}
	
	int c =10;
	int b1 =20;
	int e= 30;
	
	if(c>b1 & b1>e){
		System.out.println("c is greater");
	}
	else if(c>b1){
		System.out.println("b1 is greater");
	}
	else{
		System.out.println("c is greater");
	}
        
}
}

	
