package JavaBasics;

public class StringConcatenation {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		
		int a = 200;
		int b = 300;
		
		String c = "hello";
		String d = "world";
		
		
		System.out.println(a+b);
		System.out.println(c+d+a+b);
		System.out.println(a+d+a+c);
		
		
	}

}
